import React, { useState } from 'react';
import { useForm } from 'react-hook-form';
import { Toaster, toast } from 'react-hot-toast';
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';
import appointimg from './appontimg.png';

const bookAppointment = async (formData) => {
    try {
        // Simulating an API call, replace this with your actual API call
        // Your appointment booking logic goes here

        // Simulating success
        return true;
    } catch (error) {
        throw new Error('Failed to book appointment');
    }
};

const Appointmentbooking = () => {
    const { register, handleSubmit, formState: { errors }, watch } = useForm();
    const [startDate, setStartDate] = useState(new Date());
    const [selectedTime, setSelectedTime] = useState(null);
    const [isSubmitting, setIsSubmitting] = useState(false);

    const onSubmit = async (data) => {
        try {
            setIsSubmitting(true);
            const success = await bookAppointment(data);
            if (success) {
                // Show success toast
                toast.success('Appointment booked successfully!');
            }
        } catch (error) {
            // Show error toast if the appointment booking fails
            toast.error('Failed to book appointment. Please try again.');
        } finally {
            setIsSubmitting(false);
        }
    };

    const timePickerHandler = (time) => {
        setSelectedTime(time);
    };

    const departments = ['Pediatric', 'Gynecology', 'Cardiology'];
    const doctors = {
        Pediatric: ['Kamalesh', 'Karan'],
        Gynecology: ['Karthick'],
        Cardiology: ['Kamalesh', 'Karan', 'Karthick'],
    };

    return (
        <div className="p-4">
            <Toaster position="top-right" />
            <form onSubmit={handleSubmit(onSubmit)} className="space-y-4">
                <div className="flex space-x-4">
                    <div className="w-1/2">
                        <label htmlFor="name">Name</label>
                        <input
                            type="text"
                            id="name"
                            {...register('name', { required: 'Name is required' })}
                            className="border p-2 w-full"
                        />
                        {errors.name && <p className="text-red-500">{errors.name.message}</p>}
                    </div>
                    <div className="w-1/2">
                        <label htmlFor="phoneNumber">Phone Number</label>
                        <input
                            type="text"
                            id="phoneNumber"
                            {...register('phoneNumber', { required: 'Phone Number is required' })}
                            className="border p-2 w-full"
                        />
                        {errors.phoneNumber && <p className="text-red-500">{errors.phoneNumber.message}</p>}
                    </div>
                </div>
                <div>
                    <label>Preferred Date</label>
                    <DatePicker
                        selected={startDate}
                        onChange={(date) => setStartDate(date)}
                    />
                </div>
                <div>
                    <label>Preferred Time</label>
                    <input
                        type="time"
                        value={selectedTime}
                        onChange={(e) => timePickerHandler(e.target.value)}
                    />
                </div>
                <div className="w-1/2">
                    <label htmlFor="visit">Reason For visit</label>
                    <div>
                        <input type="radio" value="Routine CheckUp" name="visit" /> Routine CheckUp
                        <input type="radio" value="New Appointment" name="visit" /> New Appointment
                        <input type="radio" value="Specific Concern" name="visit" /> Specific Concern
                    </div>
                </div>
                <div className="mb-4">
                    <div>
                        <label htmlFor="department" className="block text-sm font-medium text-gray-700">
                            Department
                        </label>
                        <select
                            id="department"
                            {...register('department', { required: 'Please select a department' })}
                            className="w-full p-2 border"
                        >
                            <option value="">Select Department</option>
                            {departments.map((dept) => (
                                <option key={dept} value={dept}>
                                    {dept}
                                </option>
                            ))}
                        </select>
                        {errors.department && (
                            <span className="text-red-500">{errors.department.message}</span>
                        )}
                    </div>
                    <div className="mt-4">
                        <label htmlFor="doctor" className="block text-sm font-medium text-gray-700">
                            Select Doctor
                        </label>
                        <select
                            id="doctor"
                            {...register('doctor', { required: 'Please select a doctor' })}
                            className="w-full p-2 border"
                        >
                            <option value="">Select Doctor</option>
                            {watch('department') &&
                                doctors[watch('department')].map((doc) => (
                                    <option key={doc} value={doc}>
                                        {doc}
                                    </option>
                                ))}
                        </select>
                        {errors.doctor && (
                            <span className="text-red-500">{errors.doctor.message}</span>
                        )}
                    </div>
                </div>
                <button
                    type="submit"
                    className="bg-blue-500 text-white font-bold py-2 px-4 rounded"
                    disabled={isSubmitting}
                >
                    {isSubmitting ? 'Submitting...' : 'Submit'}
                </button>
            </form>

            <div style={{left: 1350,position:'absolute',maxWidth:'fillContainer',max}} >
                <div>
                    <div style={{ width: 392.83, height: 343.11, left: 256.11, top: 408.43, position: 'absolute', background: 'rgba(128.92, 185.64, 238, 0.22)', borderRadius: 9999 }} />
                    <div style={{ width: 392.83, height: 343.11, left: 269.59, top: 0, position: 'absolute', background: 'rgba(128.92, 185.64, 238, 0.22)', borderRadius: 9999 }} />
                    <div style={{ width: 392.83, height: 343.11, left: 0, top: 0, position: 'absolute', background: 'rgba(128.92, 185.64, 238, 0.22)', borderRadius: 9999 }} />
                    <div style={{ width: 382.18, height: 527, left: 65.57, top: 76.38, position: 'absolute', transform: 'rotate(1deg)', transformOrigin: '0 0' }}>
                        <div style={{ width: 374, height: 527, left: 0, top: 0, position: 'absolute', transform: 'rotate(1deg)', transformOrigin: '0 0', background: 'rgba(128.92, 185.64, 238, 0.56)', borderRadius: 10 }} />
                        <img style={{ width: 374, height: 527, left: 8.18, top: 0.14, position: 'absolute', transform: 'rotate(1deg)', transformOrigin: '0 0', borderRadius: 10 }} src="https://via.placeholder.com/374x527" />
                    </div>
                </div>
            </div>






        </div>

    );
};

export default Appointmentbooking;
