import React, { useState } from 'react';
import axios from 'axios';
import { Toaster, toast } from 'react-hot-toast';

function PatientForm() {
    const [formData, setFormData] = useState({
        firstName: '',
        lastName: '',
        email: '',
        contactNumber: '',
        AdmitTime: '',
        AdmitDate: '',
        gender: '',
        paymentMethod: '',
        hasInsurance: '',
        insurancePolicy: Null,
        wardNumber: '',
        amount: ''
    });
    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({
            ...formData,
            [name]: value
        });
    };
    const handleSubmit = (e) => {
        e.preventDefault();
        console.log(formData)
        axios.post('http://localhost:8000/patientform', formData)
            .then(res => {
                if (res.data.Status === 'Success') {
                    toast.success("Successfully Booked");
                }
                else {
                    toast.error("Booking Failed!");
                }
            })
            .catch(err => {
                console.log('Error', err);
            })
    };
    return (
        <>
            <div className="outer">
                <Toaster position='top-right'></Toaster>
                <form className="form" onSubmit={handleSubmit}>
                    <h1 className="name">Patient Details</h1>
                    <div className="flex">
                        <h1>First Name </h1>
                        <input
                            required
                            type="text"
                            className="input"
                            name='firstName'
                            value={formData.firstName}
                            onChange={handleChange}
                        />

                        <h1>LastName</h1>
                        <input
                            required
                            type="text"
                            className="input"
                            name='lastName'
                            value={formData.lastName}
                            onChange={(e) => setLastName(e.target.value)}
                        />

                    </div>
                    <div className="radio">
                        <h1>Gender</h1>
                        <input
                            type="radio"
                            value="male"
                            name="gender"
                            checked={formData.gender === 'male'}
                            onChange={handleChange}
                        />
                        <label htmlFor="male">Male</label>

                        <input
                            type="radio"
                            value="female"
                            name="gender"
                            checked={formData.gender === 'Female'}
                            onChange={handleChange}
                        />
                        <label htmlFor="female">Female</label>
                    </div>
                    <div className="flex1">
                        <h1>Email</h1>
                        <input
                            required
                            type="email"
                            className="input"
                            name='email'
                            value={formData.email}
                            onChange={handleChange}
                        />

                        <br></br>
                        <label htmlFor='contactNumber'>Contact Number </label>
                        <input
                            required
                            type="tel"
                            name='contactNumber'
                            className="input"
                            value={formData.contactNumber}
                            onChange={handleChange}
                        />

                        <div className="flex5">
                            <label htmlFor='AdmitDate'>Admit Date</label>
                            <input
                                required
                                type="date"
                                className="input"
                                name='AdmitDate'
                                value={formData.AdmitDate}
                                onChange={handleChange}
                            />
                        </div>
                        <label htmlFor='AdmitTime'>Admit Time</label>
                        <input
                            required
                            placeholder=""
                            type="time"
                            className="input"
                            name='AdmitTime'
                            value={formData.AdmitTime}
                            onChange={handleChange}
                        />
                    </div>
                    <div className="form1">
                        <h1 className="registration">Payment Details</h1>
                        <div className="payment-method">
                            <label htmlFor='paymentMethod'>Payment Method:</label>
                            <select
                                name='paymentMethod'
                                value={formData.paymentMethod}
                                onChange={handleChange}
                            >
                                <option value="online">Online</option>
                                <option value="offline">Offline</option>
                            </select>
                        </div>
                        <div className="insurance-radio">
                            <label >Do you have insurance?</label>
                            <label>
                                <input
                                    type="radio"
                                    value="yes"
                                    name="hasInsurance"
                                    checked={formData.hasInsurance === 'yes'}
                                    onChange={handleChange}
                                />
                                Yes
                            </label>

                            <label>
                                <input
                                    type="radio"
                                    value="no"
                                    name="hasInsurance"
                                    checked={formData.hasInsurance === 'no'}
                                    onChange={handleChange}
                                />
                                No
                            </label>
                        </div>

                        {hasInsurance === 'yes' && (
                            <div className="insurance-policy">
                                <label htmlFor='insurancePolicy'>Insurance Number</label>
                                <input
                                    required
                                    name='insurancePolicy'
                                    id='insurancePolicy'
                                    type="text"
                                    className="input"
                                    value={formData.insurancePolicy}
                                    onChange={handleChange}
                                />
                            </div>
                        )}
                        <div className="ward">
                            <label> Ward Number </label>
                            <input
                                required
                                type="tel"
                                className="input"
                                name='wardNumber'
                                id='wardNumber'
                                value={formData.wardNumber}
                                onChange={handleChange}
                            />

                        </div>
                        <div className="ward">
                            <label htmlFor='amount'>Advance Payment  </label>
                            <input
                                required
                                type="tel"
                                name='amount'
                                id='amount'
                                className="input"
                                value={formData.amount}
                                onChange={handleChange}
                            />
                        </div>
                        <button type="submit">Submit</button>
                    </div>
                </form>
            </div>
        </>
    )

}
export default PatientForm;
