import React, { useState } from 'react';
import { useForm } from 'react-hook-form';
import { ToastContainer, toast } from 'react-hot-toast';
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';

const bookAppointment = async (formData) => {
    try {
        // Simulating an API call, replace this with your actual API call
        // Your appointment booking logic goes here

        // Simulating success
        return true;
    } catch (error) {
        throw new Error('Failed to book appointment');
    }
};

const Appointmentbooking = () => {
    const { register, handleSubmit, formState: { errors }, watch } = useForm();
    const [startDate, setStartDate] = useState(new Date());
    const [selectedTime, setSelectedTime] = useState(null);
    const [isSubmitting, setIsSubmitting] = useState(false);

    const onSubmit = async (data) => {
        try {
            setIsSubmitting(true);
            const success = await bookAppointment(data);
            if (success) {
                // Show success toast
                toast.success('Appointment booked successfully!');
            }
        } catch (error) {
            // Show error toast if the appointment booking fails
            toast.error('Failed to book appointment. Please try again.');
        } finally {
            setIsSubmitting(false);
        }
    };

    const timePickerHandler = (time) => {
        setSelectedTime(time);
    };

    const departments = ['Pediatric', 'Gynecology', 'Cardiology'];
    const doctors = {
        Pediatric: ['Kamalesh', 'Karan'],
        Gynecology: ['Karthick'],
        Cardiology: ['Kamalesh', 'Karan', 'Karthick'],
    };

    return (
        <div className="p-4">
            <form onSubmit={handleSubmit(onSubmit)} className="space-y-4">
                <div className="flex">
                    <div className="p-2">
                        <h1 className="font-[16px] pb-2 pt-2">Name</h1>
                        <input
                            className="bg-[#FAFBFC] border border-gray-400 rounded-lg p-2 w-[400px]"
                            placeholder="Please enter the full name"
                        />
                        <h1 className="font-[16px] pb-2 pt-2">Preferred Date</h1>
                        <DatePicker
                            selected={startDate}
                            onChange={(date) => setStartDate(date)}
                        />
                    </div>
                    <div>
                        <div className="flex" style={{left:50}}>
                            <div className="p-2">
                                <h1 className="font-[16px] pb-2 pt-2">Phone Number</h1>
                                <input
                                    className="bg-[#FAFBFC] border border-gray-400 rounded-lg p-2 w-[400px]"
                                    placeholder="Please enter your phone Number"
                                />
                                <h1 className="font-[16px] pb-2 pt-2">Preferred Time</h1>
                                <div>
                                    <input
                                        type="time"
                                        value={selectedTime}
                                        onChange={(e) => timePickerHandler(e.target.value)} />
                                    <span>:</span>
                                    <input
                                        type="time"
                                        value={selectedTime}
                                        onChange={(e) => timePickerHandler(e.target.value)} />

                                </div>


                            </div>
                            <div className="w-1/2">
                                <label htmlFor="visit">Reason For visit</label>
                                <div >
                                    <input type="radio" value="Routine CheckUp" name="visit" /> Male
                                    <input type="radio" value="New Appointment" name="visit" /> Female
                                    <input type="radio" value="Specific Concern" name="visit" /> Other
                                </div>
                            </div>
                            <div className="mb-4">
                                <div>
                                    <label htmlFor="department" className="block text-sm font-medium text-gray-700">
                                        Department
                                    </label>
                                    <select
                                        id="department"
                                        {...register('department', { required: 'Please select a department' })}
                                        className="w-full p-2 border"
                                    >
                                        <option value="">Select Department</option>
                                        {departments.map((dept) => (
                                            <option key={dept} value={dept}>
                                                {dept}
                                            </option>
                                        ))}
                                    </select>
                                    {errors.department && (
                                        <span className="text-red-500">{errors.department.message}</span>
                                    )}
                                </div>
                                <div className="mt-4">
                                    <label htmlFor="doctor" className="block text-sm font-medium text-gray-700">
                                        Select Doctor
                                    </label>
                                    <select
                                        id="doctor"
                                        {...register('doctor', { required: 'Please select a doctor' })}
                                        className="w-full p-2 border"
                                    >
                                        <option value="">Select Doctor</option>
                                        {watch('department') &&
                                            doctors[watch('department')].map((doc) => (
                                                <option key={doc} value={doc}>
                                                    {doc}
                                                </option>
                                            ))}
                                    </select>
                                    {errors.doctor && (
                                        <span className="text-red-500">{errors.doctor.message}</span>
                                    )}
                                </div>
                            </div>
                            <button
                                type="submit"
                                className="bg-blue-500 text-white font-bold py-2 px-4 rounded"
                                disabled={isSubmitting}
                            >
                                {isSubmitting ? 'Submitting...' : 'Submit'}
                            </button>
                        </div>
                    </div>
                </div>
            </form>
        </div>
    );
};
export default Appointmentbooking;
