import React, { useState } from 'react';
import { Toaster, toast } from 'react-hot-toast';
// import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';
import Appointimg from './appontimg.png';
import axios from 'axios';

const Appointmentbooking = () => {
    const [formData, setFormData] = useState({
        Name: '',
        email: '',
        preferredDate: '',
        phoneNumber: '',
        startTime: '',
        endTime: '',
        reasonForVisit: '',
        department: '',
        doctor: ''
    });

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({
            ...formData,
            [name]: value
        });
    };

    // const handleDateChange = (date) => {
    //     setFormData({
    //         ...formData,
    //         preferredDate: date
    //     });
    // };
    const formatDateForInput = (date) => {
        const year = date.getFullYear();
        let month = date.getMonth() + 1;
        let day = date.getDate();

        month = month < 10 ? `0${month}` : month;
        day = day < 10 ? `0${day}` : day;

        return `${year}-${month}-${day}`;
    };
    const handleSubmit = (e) => {
        e.preventDefault();
        console.log(formData)
        axios.post('http://localhost:8000/bookappointment', formData)
            .then(res => {
                if (res.data.Status === 'Success') {
                    toast.success("Successfully Booked");
                }
                else {
                    toast.error("Booking Failed!");
                }
            })
            .catch(err => {
                console.log('Error', err);
            })
    };

    const departments = ['Pediatric', 'Gynecology', 'Cardiology'];
    const selectedDepartment = formData.department;
    const doctors = {
        Pediatric: ['Kamalesh', 'Karan'],
        Gynecology: ['Karthick'],
        Cardiology: ['Kamalesh', 'Karan', 'Karthick'],
    };
    const visits = ['Routine CheckUp', 'New Appointment', 'Online Consultant', 'Specific Concern']
    return (
        <div className="p-4">
            <Toaster position='top-right'></Toaster>
            <form onSubmit={handleSubmit} className="space-y-4">
                <div>
                    <div className="flex">
                        <div className="p-2">
                            <h1 className="font-[16px] pb-2 pt-2">Name</h1>
                            <input
                                type='text'
                                name='Name'
                                className="bg-[#FAFBFC] border border-gray-400 rounded-lg p-2 w-[400px] "
                                placeholder="Please enter the full name"
                                onChange={handleChange}
                                value={formData.Name}
                            />
                            <h1 className="font-[16px] pb-2 pt-2">Email</h1>
                            <input
                                type='email'
                                name='email'
                                className="bg-[#FAFBFC] border border-gray-400 rounded-lg p-2 w-[400px] "
                                placeholder="Please enter the full name"
                                onChange={handleChange}
                                value={formData.email}
                            />


                            <h1 className="font-[16px] pb-2 pt-2">Preferred Date</h1>
                            <input
                                type="date"
                                name="preferredDate"
                                value={formatDateForInput(formData.preferredDate)}
                                onChange={handleChange}
                            />
                        </div>
                    </div>
                    <div>
                        <div className="flex" style={{ left: '45rem', top: "20px" }}>
                            <div className="p-2">
                                <h1 className="font-[16px] pb-2 pt-2">Phone Number</h1>
                                <input
                                    type='text'
                                    name='phoneNumber'
                                    className="bg-[#FAFBFC] border border-gray-400 rounded-lg p-2 w-[400px]"
                                    placeholder="Please enter your phone Number"
                                    value={formData.phoneNumber}
                                    onChange={handleChange}
                                />
                                <h1 className="font-[16px] pb-2 pt-2">Preferred Time</h1>
                                <div>
                                    <input
                                        type="time"
                                        name='startTime'
                                        value={formData.startTime}
                                        onChange={handleChange} />
                                    <span>:</span>
                                    <input
                                        type="time"
                                        name='endTime'
                                        value={formData.endTime}
                                        onChange={handleChange} />
                                </div>
                            </div>

                        </div>
                    </div>
                    <div className="w-1/2">
                        <label htmlFor="reasonForVisit">Reason For visit</label>
                        <select

                            name='reasonForVisit'
                            id='reasonForVisit'
                            value={formData.reasonForVisit}
                            onChange={handleChange}

                            className="w-full p-2 border"
                        >
                            <option value="">Select Reason of visit</option>
                            {visits.map((dept) => (
                                <option key={dept} value={dept}>
                                    {dept}
                                </option>
                            ))}
                        </select>

                    </div>
                    <div className="mb-4">
                        <div>
                            <label htmlFor="department" className="block text-sm font-medium text-gray-700">
                                Department
                            </label>
                            <select
                                id='department'
                                name='department'
                                value={formData.department}
                                onChange={handleChange}

                            >
                                <option value="">Select Department</option>
                                {departments.map((dept) => (
                                    <option key={dept} value={dept}>
                                        {dept}
                                    </option>
                                ))}
                            </select>

                        </div>

                        <div className="mt-4">
                            <label htmlFor="doctor" className="block text-sm font-medium text-gray-700">
                                Select Doctor
                            </label>
                            <select
                                id="doctor"
                                name='doctor'
                                value={formData.doctor}
                                onChange={handleChange}

                            >
                                <option value="">Select Doctor</option>
                                {selectedDepartment &&
                                    doctors[selectedDepartment].map((doc) => (
                                        <option key={doc} value={doc}>
                                            {doc}
                                        </option>
                                    ))}
                            </select>

                        </div>
                    </div>
                    <button
                        type="submit"
                        className="bg-blue-500 text-white font-bold py-2 px-4 rounded"
                    >
                        Submit
                    </button>
                </div>
            </form >
            <div style={{ position: 'absolute', left: '68rem', top: '30px' }} >
                <img src={Appointimg}></img>
            </div>
        </div >
    );
};
export default Appointmentbooking;
